mapper.select:
select count(*) from
free_board
where fr_writer = #{fr_writer}

select * from (
select rownum as rn, t.*
from (
select *
from
free_board
order by fr_seq desc
) t
)
where rn between #{start} and #{end}
and
fr_writer = #{fr_writer}

SELECT *
FROM (
SELECT ROW_NUMBER() OVER (ORDER BY fb.fr_seq
DESC) AS rn, fb.*
FROM free_board fb
JOIN bookmark b ON fb.fr_seq =
b.fr_seq
WHERE b.user_id = #{id}
) t
WHERE rn BETWEEN #{start} AND #{end}
