mapper.select:
SELECT count(*)
FROM (
SELECT *
FROM perfume p
LEFT JOIN perfume_image i
ON p.per_seq = i.per_seq

	where PER_BRAND IN

		#{per_brand}



	where 1=1


	AND per_price BETWEEN #{startPrice} AND #{endPrice}


	AND per_price >= #{startPrice}


	and per_price <= #{endPrice}


	where 1=1

)

SELECT *
FROM (
SELECT *
FROM perfume p
LEFT JOIN perfume_image i ON
p.per_seq = i.per_seq

	WHERE PER_BRAND IN

		#{per_brand}



	AND per_price BETWEEN #{startPrice} AND #{endPrice}



	AND per_price >= #{startPrice}


	and per_price <= #{endPrice}


	and 1=1

) p

SELECT * FROM perfume p LEFT JOIN perfume_image i ON p.per_seq = i.per_seq
WHERE PER_BRAND IN
#{per_brand}
where 1=1
AND per_price BETWEEN #{startPrice} AND #{endPrice}
AND per_price >= #{startPrice}
and per_price <= #{endPrice}
and 1=1
and per_name like '%' || #{searchText} || '%'
AND (top1 LIKE '%'||#{searchText}||'%' OR top2 LIKE '%'||#{searchText}||'%' OR top3 LIKE '%'||#{searchText}||'%' OR middle1 LIKE '%'||#{searchText}||'%' OR middle2 LIKE '%'||#{searchText}||'%' OR middle3 LIKE '%'||#{searchText}||'%' OR base1 LIKE '%'||#{searchText}||'%' OR base2 LIKE '%'||#{searchText}||'%' OR base3 LIKE '%'||#{searchText}||'%')
and 1=1
